export const useDeleteMyProfilePicture = () => {
    const getUrl = useTenantUrl()
    const loading = ref(false)

    const mutate = async () => {
      loading.value = true
      try {
        await $fetch(getUrl('/employees/profile/me/picture'), {
          method: 'DELETE',
